export function getPostgresFeatures() {
    return POSTGRES_FEATURES;
}
export function isFeatureSupported(features, feature) {
    return features[feature] === true;
}
export function getUnsupportedFeatures(features) {
    const unsupported = [];
    for (const [key, value] of Object.entries(features)) {
        if (value === false && POSTGRES_FEATURES[key] === true) {
            unsupported.push(key);
        }
    }
    return unsupported;
}
